import React from "react";
import { capitalizeFirstLetter } from "../../utils/helpers";
import headerImage from "../../assets/cover/header.jpg";

function Nav() {
  const  categories = [
    { name: 'Portfolio', description: 'My up-to-date portfolio' },
    { name: 'Resume', description: 'Past experience and job history' },

  ];

  const handleClick = () => {
    console.log("click handled")
  }

  return (
    <header data-testid="header" className="flex-row px-1">
      <h2>
        <a href="/">
          <span class="name" >@alehr45</span> 
          <img src={headerImage} className="my-2" style={{ width: "40%" }} alt="cover" />
        </a>
      </h2>
      <nav>
        <ul className="flex-row">
          <li className="mx-2">
            <a href="#about" onClick={() => handleClick()}>
              About me
            </a>
          </li>
          <li className={"mx-2"}>
            <span onClick={() => handleClick()}>
              Contact
            </span>
          </li>
          {
            categories.map((category) => (
              <li className="mx-1" key={category.name} >
                <span onClick={() => { handleClick(); }}>
                 {capitalizeFirstLetter(category.name)}
                </span>
              </li>
            ))
          }
        </ul>
      </nav>
    </header>
  );
}

export default Nav;
